import React, { useContext, useState } from "react";
import {DataTable} from "primereact/datatable";
import {Column} from "primereact/column";
import {Panel} from "primereact/panel";
import {Button} from "primereact/button";
import { EmpleadoContext } from "../contexts/EmpleadoContext";
import EmpleadoForm from "./EmpleadoForm";

const EmpleadoList = () => {

    const {empleados, findEmpleado} = useContext(EmpleadoContext);

    const [isVisible, setIsVisible] = useState(false);

    const saveEmpleado = (id) => {
        findEmpleado(id)
        setIsVisible(true)
    }

    const footer = (
        <div className="p-clearfix"  style={{width:"100%"}}>
            <Button
                style={{float:"left"}}
                icon="pi pi-plus"
                label="Add"
                onClick={() => setIsVisible(true)}
            />

        </div>
    )

    return (<div>
 <Panel header="Lista de empleados" style={{width:'80%', marginTop:'20px', margin:'0 auto'}}>
        <DataTable 
                value={empleados}
                selectionMode="single"
                onSelectionChange={(e) => saveEmpleado(e.value._id)}
                footer={footer}
                >
          <Column field="id" header="ID"></Column>
          <Column field="nombreCompleto" header="Empleado"></Column>
          <Column field="puesto" header="Puesto"></Column>
          <Column field="salario" header="Salario"></Column>
          <Column field="estatus" header="Estatus"></Column>
        </DataTable>
      </Panel>
      
  <EmpleadoForm isVisible={isVisible} setIsVisible={setIsVisible}/>
    </div>);
}

export default EmpleadoList;
