import React, {useContext, useState, useEffect} from 'react';
import { EmpleadoContext } from '../contexts/EmpleadoContext';
import {Dialog} from "primereact/dialog";
import {Button} from "primereact/button";
import {InputText} from "primereact/inputtext";
import {InputNumber} from "primereact/inputnumber";
import {Calendar} from "primereact/calendar";

const EmpleadoForm = (props) =>{

    const {isVisible, setIsVisible} = props;
    const {
        createEmpleado,
        deleteEmpleado,
        editEmpleado,
        updateEmpleado,
    } = useContext(EmpleadoContext);

    const initialEmpleadoState = {
        idTCalificaciones: 0,
        alumnoId:"",
        alumnoNombre:"",
        materiaNombre: "",
        calificacion: "",
        fechaRegistro: null,
    }
    
    const [empleadoData, setEmpleadoData] = useState(initialEmpleadoState);

    useEffect(() =>{
        if(editEmpleado) setEmpleadoData(editEmpleado);
    }, [editEmpleado])


    const updateField = (data, field) =>{
        setEmpleadoData({
            ...empleadoData,
            [field]:data
        })

        console.log(empleadoData)

    }

    const _deleteEmpleado = () => {
        if (editEmpleado) {
            deleteEmpleado(empleadoData.id);
          setEmpleadoData(initialEmpleadoState);
        }
        setIsVisible(false);
      };

    const saveEmpleado = () => {
      
    if (!editEmpleado) {
            createEmpleado(empleadoData);
        } else {
            updateEmpleado(empleadoData);
          }

        setEmpleadoData(initialEmpleadoState);
        setIsVisible(false);
    }

    const dialogFooter = (
        <div className='ui-dialog-buttonpane p-clearfix'>
        <Button label="Delete" icon="pi pi-times" onClick={_deleteEmpleado} />
            <Button label='Save' icon="pi pi-check" onClick={saveEmpleado}/>
        </div>

    )

    const clearSelected = () => {
        setIsVisible(false);
        setEmpleadoData(initialEmpleadoState);
      };
    

return (<div>
    <Dialog
        visible={isVisible}
        modal={true}
        style={{width:"420px"}}
        contentStyle={{overflow:"visible"}}
        header="Calificación"
        onHide={() => clearSelected()}
        footer={dialogFooter}
    >
        <div className='p-grid p-fluid'>
            <br/>
            <div className='p-float-label'>
                <InputText
                    value={empleadoData.alumnoNombre}
                    onChange={e => updateField(e.target.value.trim(), "nombreCompleto")}
                />
                <label>Alumno:</label>
            </div>
            <br/>
            <div className='p-float-label'>
                <InputText
                    value={empleadoData.materiaNombre}
                    onChange={e => updateField(e.target.value.trim(), "materiaNombre")}
                />
                <label>Puesto:</label>
            </div>
            <br/>
            <div className='p-float-label'>
                <InputText
                    value={empleadoData.calificacion}
                    onChange={e => updateField(e.target.value, "calificacion")}
                   // mode="currency"
                    //currency='USD'
                />
                <label>Salario:</label>
            </div>
            <br/>
            <div className='p-float-label'>
                <InputText
                    value={empleadoData.alumnoId}
                    onChange={e => updateField(e.target.value.trim(), "alumnoId")}
                />
                <label>Estatus:</label>
            </div>
            <br/>
            <div className='p-float-label'>
                <Calendar
                    value={empleadoData.fechaRegistro}
                    onChange={e => updateField(e.target.value, "fechaRegistro")}
                    dateFormat="yy-mm-dd"
                />
                <label>Fecha de contratación:</label>
            </div>
        </div>
    </Dialog>
</div>);
}


export default EmpleadoForm;
