import React, {createContext, useState, useEffect} from 'react';
import { PersonaService } from '../service/PersonaService';

export const EmpleadoContext = createContext();

const EmpleadoContextProvider = (props) => {
    const empleadoService = new PersonaService();

const [empleados,setEmpleados] = useState([]);

const [editEmpleados,setEditEmpleados] = useState(null);



const createEmpleado = (empleado) =>{
    empleadoService
    .create(empleado)
    .then((data) => setEmpleados([...empleados, data]))
}

const deleteEmpleado = (id) =>{
    empleadoService
    .delete(id)
    .then((data) => setEmpleados(empleados.filter((p) => p.id !== id )))
}

const findEmpleado = (id) =>{
    const empleado = empleados.find(p => p.id === id);
    setEditEmpleados(empleado);
}

const updateEmpleado = (empleado) =>{
    empleadoService
    .update(empleado)
    .then((data) => 
    setEmpleados(
        empleados.map(p => p.id === empleado.id ? data : empleado)
        ));

    setEditEmpleados(null);
}

    return (
        <EmpleadoContext.Provider
        value={{
            createEmpleado,
            deleteEmpleado,
            findEmpleado,
            updateEmpleado,
            empleados
        }}
        >
            {props.children}
        
        </EmpleadoContext.Provider>
    );
};

export default EmpleadoContextProvider;
